import React, { useEffect, useState } from 'react'
import { getGifs } from '../helpers/getGifs';
import { GifGridItem } from './GifGridItem';


export const GifGrid = ({ category }) => {

    const [images, setImages] = useState([]);


    useEffect(() => {
        getGifs();
    }, [])


    return (
        <>
            <h3>{category}</h3>

            <div className='card-grid'>
                <ol>
                    {
                        images.map((img) => (
                            <GifGridItem
                                key={img.id}
                                {...img} />
                        ))
                    }

                </ol>

            </div>
        </>
    )
}
