import { getGifs } from "../helpers/getGifs";



export const useFetchGifs = (category) => {

    const [state, setState] = useState({
        data: [],
        loading: true
    });

    useEffect(() => {
        getGifs(category)
        .then( imgs => {

            setTimeout( () => {
                setState({
                    data: imgs,
                    loading: false
                })
            }, 3000);
        });

        console.log(data);

    }, [category])



    return state;
}
